import React, { useContext, useState,useEffect } from 'react';
import './Dashboard.css';
import { UserContext } from "../UserContext";
import axios from 'axios'


const Dashboard = () => {
    const [tickets, setTickets] = useState([]); // Empty array to store fetched tickets
    const [searchQuery, setSearchQuery] = useState('');
    const [statusFilter, setStatusFilter] = useState('');
    const {user,setUser}=useContext(UserContext)
    
 
    // Fetch tickets from the backend when the component mounts
    useEffect(() => {
        const fetchTickets = async () => {
            console.log(user)
            try {
                const response = await axios.get(`http://localhost:5000/api/tickets/customer/${user._id}`);
                console.log('Tickets fetched:', response.data); // Log the data
                setTickets(response.data);
            } catch (error) {
                console.error('Error fetching tickets:', error);
            }
        };
   
        fetchTickets();
    }, []);
   
 
    // Handle changes in the search input
    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };
 
    // Handle changes in the status filter dropdown
    const handleStatusChange = (e) => {
        setStatusFilter(e.target.value);
    };
 
    // Filter tickets based on search query and status filter
    const filteredTickets = tickets.filter(ticket =>
        (statusFilter === '' || ticket.status === statusFilter) &&
        ticket.subject.toLowerCase().includes(searchQuery.toLowerCase())
    );
 
    return (
        <div className="dashboard-container">
            <header className="header">
                <div className="logo">TelecomCo</div>
                <nav className="nav-menu">
                    <a href="#home">Home</a>
                    <a href="#tickets">Tickets</a>
                    <a href="#reports">Reports</a>
                    <a href="#settings">Settings</a>
                </nav>
                <div className="profile-photo">
                    <img src="profile-photo-url" alt="Profile" />
                </div>
            </header>
 
            <main className="main-content">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        value={searchQuery}
                        onChange={handleSearchChange}
                    />
                    <select
                        className="status-filter"
                        value={statusFilter}
                        onChange={handleStatusChange}
                    >
                        <option value="">All Statuses</option>
                        <option value="To-Do">To-Do</option>
                        <option value="In-Progress">In-Progress</option>
                        <option value="Closed">Closed</option>
                    </select>
                    <button className="raise-ticket-button">Raise Ticket</button>
                </div>
                <table className="info-table">
                    <thead>
                        <tr>
                            <th>Ticket No.</th>
                            <th>Subject</th>
                            <th>Status</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredTickets.length > 0 ? (
                            filteredTickets.map(ticket => (
                                <tr key={ticket.ticketNo}>
                                    <td>{ticket.ticketNo}</td>
                                    <td>{ticket.subject}</td>
                                    <td>{ticket.status}</td>
                                    <td>{ticket.date}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="4">No tickets found.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </main>
        </div>
    );
};
 
export default Dashboard;
