import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import './Login.css';
import { UserContext } from "../UserContext";
import axios from 'axios';
import api from '../api.js'
 
const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isAgent, setIsAgent] = useState(false); // New state to track if the user is an agent
    const [errorMessage, setErrorMessage] = useState('');
    const { user,setUser } = useContext(UserContext);
 
    const navigate = useNavigate(); // Initialize useNavigate
 
    const validateEmail = (email) => {
        return /\S+@\S+\.\S+/.test(email);
    };
 
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            if (!validateEmail(email)) {
                setErrorMessage('Please enter a valid email address.');
                return;
            }
            if (password.length < 6) {
                setErrorMessage('Password must be at least 6 characters long.');
                return;
            }
 
            const { data } = await api.post('/auth/login', { email, password });
 
            console.log(data.user, data.token);
            setUser(data.user);
 
            // Check if the user is an agent and navigate accordingly
            if (isAgent) {
                navigate('/dashboardAgent');
            } else {
                navigate('/dashboard');
            }
        } catch (e) {
            console.log(e);
            alert("Login Failed");
        }
    };
 
    return (
        <div className="login-container">
            <div className="side-panel">
                <h1>Welcome to TelecomCo</h1>
                <p className="subtitle">We're holding the door for you! Login now and manage all your TelecomCo services with ease.</p>
            </div>
            <form className="login-form" onSubmit={handleLogin}>
                <h2>Login to TelecomCo</h2>
 
                {errorMessage && <p className="error-message">{errorMessage}</p>}
 
                <div className="input-group">
                    <label htmlFor="email">Email Address</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
 
                {/* Add the checkbox for agent role */}
                <div className="input-group-inline">
    <input
        type="checkbox"
        checked={isAgent}
        onChange={() => setIsAgent(!isAgent)}
    />
    <label>I am an agent</label>
</div>
 
 
                <button type="submit" className="login-button">Login</button>
 
                <p className="forgot-password">
                    <a href="#forgot-password">Forgot Password?</a>
                </p>
 
                <div className="sign-up-link">
                    <a href="#signup">Sign Up</a>
                </div>
            </form>
        </div>
    );
};
 
export default Login;
