import React, { useEffect, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
import api from '../api'; // Ensure this is correctly configured for your API
import { UserContext } from "../UserContext";

const Dashboard = () => {
    const [tickets, setTickets] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [statusFilter, setStatusFilter] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false); // State to control pop-up visibility
    const [formData, setFormData] = useState({ subject: '', description: '', photo: null }); // Form data
    const { user, ready } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchTickets = async () => {
            if (!ready) return;

            if (!user) {
                navigate('/');
                return;
            }

            setLoading(true);
            setError(null);

            try {
                const response = await api.get(`/tickets/customer/${user._id}`);
                console.log('Tickets fetched:', response.data);
                setTickets(response.data);
            } catch (error) {
                console.error('Error fetching tickets:', error);
                setError('Error fetching tickets.');
            } finally {
                setLoading(false);
            }
        };

        fetchTickets();
    }, [ready, user, navigate]);

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value || ''); // Ensure searchQuery is always a string
    };

    const handleStatusChange = (e) => {
        setStatusFilter(e.target.value || ''); // Ensure statusFilter is always a string
    };

    const filteredTickets = tickets.filter(ticket => {
        // Safeguard against undefined values
        const ticketTitle = ticket.title || '';
        const ticketStatus = ticket.status || '';
        
        return (
            (statusFilter === '' || ticketStatus === statusFilter) &&
            ticketTitle.toLowerCase().includes(searchQuery.toLowerCase())
        );
    });

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Form Data:', formData); // Implement API call to submit form data
         await api.post('/tickets', {title:formData.subject,description:formData.description,attachments:[],customerId:user._id,category:formData.category});
        closePopup();
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleFileChange = (e) => {
        setFormData({ ...formData, photo: e.target.files[0] });
    };

    const openPopup = () => {
        setIsPopupOpen(true);
    };

    const closePopup = () => {
        setIsPopupOpen(false);
    };

    const handleLogout = () => {
        // Clear authentication-related data
        sessionStorage.clear(); // Adjust as needed
        // Redirect to login page
        navigate('/');
    };

    return (
        <div className="dashboard-container">
            <header className="header">
                <div className="logo">TelecomCo</div>
                <nav className="nav-menu">
                    <a href="#home">Home</a>
                    <a href="#tickets">Tickets</a>
                    <a href="#reports">Reports</a>
                    <a href="#settings">Settings</a>
                </nav>
                <div className="profile-photo">
                    <img src="profile-photo-url" alt="Profile" />
                </div>
                <button className="logout-button" onClick={handleLogout}>Logout</button>
            </header>

            <main className="main-content">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        value={searchQuery}
                        onChange={handleSearchChange}
                    />
                    <select className="status-filter" value={statusFilter} onChange={handleStatusChange}>
                        <option value="">All Status</option>
                        <option value="To-Do">To-Do</option>
                        <option value="In-Progress">In-Progress</option>
                        <option value="Closed">Closed</option>
                    </select>
                    <button className="raise-ticket-button" onClick={openPopup}>
                        Raise Ticket
                    </button>
                </div>

                <table className="info-table">
                    <thead>
                        <tr>
                            <th>Ticket No.</th>
                            <th>Subject</th>
                            <th>Status</th>
                            <th>Agent Name</th>
                            <th>Agent Email</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredTickets.length > 0 ? (
                            filteredTickets.map(ticket => (
                                <tr key={ticket.ticketNo}>
                                    <td>{ticket._id}</td>
                                    <td>{ticket.title}</td>
                                    <td>{ticket.status}</td>
                                    <td>{ticket.agent.name}</td>
                                    <td>{ticket.agent.email}</td>
                                    <td>{ticket.createdAt}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="4">No tickets found.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </main>

            {/* Pop-up for Raise Ticket */}
            {isPopupOpen && (
      <div className="popup-overlay">
        <div className="popup-content">
          <h2>Raise a Ticket</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="subject">Subject</label>
            <input
              type="text"
              id="subject"
              name="subject"
              value={formData.subject}
              onChange={handleInputChange}
              required
            />

            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              rows="4"
              value={formData.description}
              onChange={handleInputChange}
              required
            ></textarea>

            <label htmlFor="category">Category</label>
            <select
              id="category"
              name="category"
              value={formData.category}
              onChange={handleInputChange}
              required
            >
              <option value="Technical">Technical</option>
              <option value="Billing">Billing</option>
              <option value="General">General</option>
              <option value="Product">Product</option>
            </select>

            <label htmlFor="photo">Upload Photo</label>
            <input
              type="file"
              id="photo"
              name="photo"
              accept="image/*"
              onChange={handleFileChange}
            />

            <div className="popup-actions">
              <button type="submit">Submit</button>
              <button type="button" onClick={closePopup}>Close</button>
            </div>
          </form>
        </div>
      </div>
    )
  }
        </div>
    );
};

export default Dashboard;
